const deviceCards=document.querySelectorAll(".devices-item"),deviceLinks=document.querySelectorAll(".devices-button");for(let e of deviceCards)e.addEventListener("mouseover",e=>{e.target.closest(".devices-item").querySelector(".devices-button-wrapper").classList.remove("visually-hidden")}),e.addEventListener("mouseout",e=>{e.target.closest(".devices-item").querySelector(".devices-button-wrapper").classList.add("visually-hidden")});for(let e of deviceLinks)e.addEventListener("focus",e=>{e.target.closest(".devices-button-wrapper").classList.remove("visually-hidden")}),e.addEventListener("blur",e=>{e.target.closest(".devices-button-wrapper").classList.add("visually-hidden")});