const $searchForm=document.querySelector(".header-search"),$searchField=$searchForm["search-field"],$searchSubmit=$searchForm.querySelector(".header-search-submit");$searchField.addEventListener("focus",()=>{$searchSubmit.classList.add("header-search-submit_active"),$searchForm.classList.add("header-search_active"),$searchForm.reset()}),$searchForm.addEventListener("submit",e=>{$searchSubmit.classList.remove("header-search-submit_active"),$searchForm.classList.remove("header-search_active")});